INSERT INTO phpbb_mods_database (mod_id, mod_title, mod_version, mod_version_type, mod_desc, mod_url, mod_author, mod_download, mod_phpbb_version, mod_comments, mod_access, mod_author_email, mod_install_date) VALUES (1, '1 phpBB', '3.0.15', '', 'phpBB is the core of integramod.\n\nphpBB is a free flat-forum bulletin board software solution that can be used to stay in touch with a group of people or can power your entire website. With an extensive database of user-created mods and a styles database containing hundreds of style and image packages to customize your board, you can create a very unique forum in minutes.', 'https://www.phpbb.com/', 'phpBB Group', 'https://github.com/phpbb/phpbb/tree/3.0.x/phpBB', '3.0.14', '', 0, '', 0),(2, '3 IntegraMOD Portal (aka KISS Portal)', '3.0.15', '', 'An advanced, fully integrated, portal for phpBB 3 written entirely by one of our key Developers.\nThe portal is packed with a host of features all of which are configurable via portal tab in the ACP', '', 'michaelo', 'https://github.com/phpbbireland/stargate-portal', '3.0.14', '', 0, 'o2l@eircom.net', 0),(3, 'Mods Database', '0.2.1', '', 'Adds a simple Mods Database for keeping track of the mods installed on the board with possibility to add various additional information.\nMod detail page to view the additional information, founders will be able to see installation date on mod detail page. Possibility to show certain mods to admins only', '', 'lefty74', '', '', '', 0, 'lefty@lefty74.com', 0),(24, 'Multi Selection Profile Fields', '1.0.0', '', 'Adds a new mode to custom profile field system for multible choice options', 'https://djchrisnet.de/', 'Christoph Kühl', '', '3.0.15', '', 0, 'info@djchrisnet.de', 0),(5, 'Download Mod', '6.5.32', '', 'This mod generates a page of downloads for your phpBB 3 forum', 'http://www.oxpus.de', 'OXPUS', '', '3.0.14', '', 0, 'webmaster@oxpus.de', 0),(6, 'Meeting MOD', '2.0.20', '', 'Implement a meeting managemant. \nFor example to initiate a board meeting with your users', 'http://www.oxpus.de', 'OXPUS', '', '3.0.14', '', 0, 'webmaster@oxpus.de', 0),(7, 'Advanced BBCode Box 3', '3.0.13', '', 'ABBC3 replaces phpBB3\'s BBCode buttons with icons in a beautiful and highly customizable toolbar. ABBC3 also adds many new and useful BBCodes giving users more ways to customize their posts. It also improves some of phpBB3\'s built-in BBCodes and supports your own custom BBCodes. \nAdditional features include an Image Resizer with various Lightbox styled expanders, and Auto-Embed Video for dozens of media sites and file formats. \nABBC3 is a sleek addition to your phpBB bulletin board.', 'https://www.phpbb.com/customise/db/mod/advanced_bbcode_box_3', 'VSE &amp; leviatan21', 'https://github.com/VSEphpbb/Advanced-BBCode-Box-3', '3.0.14', '', 0, 'info@mssti.com', 0),(8, 'Personal Notes', '2.0.11', '', 'Want personal notes? Here it is...', 'http://www.oxpus.de', 'OXPUS', '', '3.0.14', '', 0, 'webmaster@oxpus.de', 0),(9, 'phpBB Gallery', '1.1.6', '', 'An image-gallery integrated into your phpbb-board.\n\nFeatures: http://www.flying-bits.org/go/phpbb_gallery/features', 'http://www.flying-bits.org', 'nickvergessen', '', '3.0.14', '', 0, 'nickvergessen@gmx.de', 0),(10, 'phpBB3-Knowledgebase', '1.0.5', '', 'This mod adds a Knowledge Base to your forum, which is fully integrated with phpBB\'s core system.', '', 'Tobi Sch&amp;auml;fer', '', '3.0.14', '', 0, '', 0),(16, 'Users of the day', '0.0.2', '', 'This mod displays, under the online users list, a list of all the registered users who visited during the last XX hours.\nIt can also display the list of the users who didn\'t come.', 'http://stotis.net', 'Justin', '', '3.0.14', '', 0, 'sanis@portalas.us', 0),(11, 'bbGeSHi', '0.7.5', '', 'a better syntax highlighter with code line numbers.\n\nThis MOD uses a modified version of the latest stable* GeSHi release 1.0.7.20.\nGeSHi and information about it can be found at http://qbnz.com/highlighter/', 'http://www.phpportalen.net', 'DarrenSW', '', '3.0.14', '', 0, '', 0),(12, 'AJAX Chat', '0.8', '', 'Adds a chat page to your forum', '', 'Sebastian Tschan', '', '3.0.14', '', 0, '', 0),(13, 'AJAX Registration Checks', '0.1.2', '', 'During user registration, this MOD makes a number of checks to see if it can find errors with the information given by the user. These checks are;\n* Checks if the username is available or has already been taken.\n* Checks that the two passwords entered are the same (if both have been entered).\n* Checks if the first email address is in the correct format. If it is then it does the next check:\n* Checks that the two email addresses entered are the same (if both have been entered).', '', 'Luke Cousins (LoonyLuke)', '', '3.0.14', '', 0, '', 0),(22, 'ACP file backup', '1.0', '', 'Utility for quickly backing up phpbb3 files via the ACP. Directories and files in the root phpBB3 directory can be selected to add to a \nzip archive which can be stored locally, downloaded or both.\nThis eliminates the need to download all your files seperately via an FTP client. For users without SSH, and for users without any facility \nto compress files online, this utility will offer significant time savings in backing up files, as downloading a single archive requires \nsignificantly less overhead than downloading each individual file.', '', 'livewirestu  (Stuart McConnel)', '', '3.0.15', '', 0, 'stumcconnel@sky.com', 0),(23, 'Groups on Registration and Custom Profile Fields', '1.0.1', '', 'This mod can display groups on registration screen to join. Displayed groups cannot be special groups; request, closed and hidden type group membership requests need to be approved. It can also brings the ability of adding users to groups based on custom profile fields data. Whenever a user\'s custom profile fields data is changed, custom profile fields groups are updated. Admininistrators can batch add/remove users to/from custom profile fields based groups at ACP.', '', 'mtrs', '', '3.0.15', '', 0, 'trst2007@gmail.com', 0),(15, 'Ajax ShoutBox', '1.0.3a', '', 'This mod add a shoutbox to your forum index.\n \n\nShoutbox portal block by HelterSkelter', '', 'Paul Sohier', '', '3.0.14', '', 0, '', 0),(17, 'Topic Calendar', '2.0.0b1', '', 'Adds a calendar to you forum with the ability to add events and post topic events', '', 'LivewireStu', '', '3.0.14', '', 0, '', 0),(18, 'ACP Selectable Mod Links in Header', '1', '', 'Allows administrators to select which mod links to display in the overall header and to decide which groups can see them.', 'https://www.integramod.com', 'Helter', '', '3.0.14', '', 0, '', 0),(19, '2 phpBB3 core updates', '3.0.15', '', 'IntegraMOD3 incorporates phpBB3.0.14 core fixes written by Dion of dion-designs.com\n\nWhile doing some research during my tedious task of updating the core to support php 7.4 I found many posts directing me to dion-designs.com. Dion\'s updates were superior to mine and pushed php support to version 8.', 'https://forum.dion-designs.com/index.php', 'DionDesigns', '', '3.0.14', '', 0, '', 0),(20, 'phpBB Digests', '2.2.9', '', 'Sends daily or weekly digests of posts to subscribers', 'http://phpbbservices.com/digests/', 'Mark D Hamill', '', '3.0.15', '', 0, 'mark@phpbbservices.com', 0),(21, 'Advertisement Management', '1.0.10', '', 'Adds an easy to use Advertisement Manager to a phpBB3 forum', 'https://www.phpbb.com/community/memberlist.php?mode=viewprofile&amp;un=EXreaction', 'Nathan Guse', '', '3.0.15', '', 0, 'exreaction@lithiumstudios.org', 0),(25, 'phpBB mini Chat', '1.3.8', '', 'Мini-chat for the forum index and seperate page.', 'https://www.phpbb.com/community/memberlist.php?mode=viewprofile&amp;un=RMcGirr83', 'Rich McGirr', '', '3.0.15', '', 0, 'rmcgirr83@gmail.com', 0),(26, 'FAQ Manager', '1.2.4', '', 'Adds an easy to use FAQ Manager to the ACP.', '', 'EXreaction  (Nathan Guse)', '', '3.0.15', '', 0, '', 0),(27, 'First post on every page', '1.2.2', '', 'This mod allows to show first post of topic on top of it\'s every page in phpBB3.', 'http://phpbbguru.net', 'rxu', '', '3.0.15', '', 0, 'rxu@sunlan.ru', 0),(28, 'Who posted?', '1.0.1', '', 'This MOD will allow users to view who posted in a topic.', '', 'eviL&lt;3 (Igor Wiedler)', '', '3.0.15', '', 0, 'evil@phpbbmodders.net', 0),(29, 'Show Password Strength', '1.0.3', '', 'This MOD will show users how strong (or weak) their password is as they type it into the password field. The password field will display a color code (red for weak to green for strong) as well as a text indicator for \'Very Weak\', \'Weak\', \'Good\', \'Strong\' and \'Very Strong\' passwords. This feature is active on new user registration, and in the UCP and ACP areas where you can change existing passwords to new passwords.', 'http://www.phpbb.com/customise/db/mod/show_password_strength/', 'VSE (Matt Friedman)', '', '3.0.15', '', 0, '', 0);

# Table: phpbb_modules
DROP TABLE IF EXISTS phpbb_modules;
CREATE TABLE `phpbb_modules` (
  `module_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `module_enabled` tinyint(1) unsigned NOT NULL DEFAULT 1,
  `module_display` tinyint(1) unsigned NOT NULL DEFAULT 1,
  `module_basename` varchar(255) NOT NULL DEFAULT '',
  `module_class` varchar(10) NOT NULL DEFAULT '',
