em;\r\n	background: none 0 50% no-repeat;\r\n}\r\n\r\n.row .pagination span a, li.pagination span a {\r\n	background-color: #FFFFFF;\r\n}\r\n\r\n.row .pagination span a:hover, li.pagination span a:hover {\r\n	background-color: #d2d2d2;\r\n}\r\n\r\n/* Miscellaneous styles\r\n---------------------------------------- */\r\n#forum-permissions {\r\n	float: right;\r\n	width: auto;\r\n	padding-left: 5px;\r\n	margin-left: 5px;\r\n	margin-top: 10px;\r\n	text-align: right;\r\n}\r\n\r\n.copyright {\r\n	padding: 5px;\r\n	text-align: center;\r\n	color: #555555;\r\n}\r\n\r\n.small {\r\n	font-size: 0.9em !important;\r\n}\r\n\r\n.titlespace {\r\n	margin-bottom: 15px;\r\n}\r\n\r\n.headerspace {\r\n	margin-top: 20px;\r\n}\r\n\r\n.error {\r\n	color: #bcbcbc;\r\n	font-weight: bold;\r\n	font-size: 1em;\r\n}\r\n\r\n.reported {\r\n	background-color: #f7f7f7;\r\n}\r\n\r\nli.reported:hover {\r\n	background-color: #ececec;\r\n}\r\n\r\ndiv.rules {\r\n	background-color: #ececec;\r\n	color: #bcbcbc;\r\n	padding: 0 10px;\r\n	margin: 10px 0;\r\n	font-size: 1.1em;\r\n}\r\n\r\ndiv.rules ul, div.rules ol {\r\n	margin-left: 20px;\r\n}\r\n\r\np.rules {\r\n	background-color: #ececec;\r\n	background-image: none;\r\n	padding: 5px;\r\n}\r\n\r\np.rules img {\r\n	vertical-align: middle;\r\n	padding-top: 5px;\r\n}\r\n\r\np.rules a {\r\n	vertical-align: middle;\r\n	clear: both;\r\n}\r\n\r\n\r\n/* Start Mod Breizh Ajax Checks */\r\n#ajaxfrom {\r\n	display: none;\r\n}\r\n\r\n.checks_details {\r\n	display: block;\r\n	margin-top: 8px;\r\n}\r\n\r\n.checks_print {\r\n	margin: 1em 0 0 17em;\r\n}\r\n/* END Mod Breizh Ajax Checks */\r\n#top {\r\n	position: absolute;\r\n	top: -20px;\r\n}\r\n\r\n.clear {\r\n	display: block;\r\n	clear: both;\r\n	font-size: 1px;\r\n	line-height: 1px;\r\n	background: transparent;\r\n}\r\n/**\r\n* Inline Diff\r\n*/\r\n.ins {\r\n	background-color: #dfd;\r\n	text-decoration: underline;\r\n}\r\n\r\n.del {\r\n	background-color: #fdd;\r\n	text-decoration: line-through;\r\n}\r\n\r\n.diff_codepanel {\r\n	overflow: auto;\r\n	width: 100%;\r\n	height: 350px;\r\n	display: inline-block;\r\n}\r\n\r\n.resultDIV {\r\n	position: absolute;\r\n	width: 195px;\r\n	z-index: 1;\r\n	border-style: solid;\r\n	border-width: 1px;\r\n	background-color:#FFFFFF\r\n}\r\n/* PORTAL CSS CLASSES */\r\n\r\nul.links li{\r\n	line-height: 2.2em;\r\n}\r\n\r\nul.links li img{\r\n	margin: 0 1px -2px 0;\r\n} \n /* END @include common.css */ \n\r\n/* BEGIN @include links.css */ \n /* Link Styles\r\n---------------------------------------- */\r\n\r\n/* Links adjustment to correctly display an order of rtl/ltr mixed content */\r\na {\r\n	direction: ltr;\r\n	unicode-bidi: embed;\r\n}\r\n\r\na:link	{ color: #898989; text-decoration: none; }\r\na:visited	{ color: #898989; text-decoration: none; }\r\na:hover	{ color: #d3d3d3; text-decoration: underline; }\r\na:active	{ color: #d2d2d2; text-decoration: none; }\r\n\r\n/* Coloured usernames */\r\n.username-coloured {\r\n	font-weight: bold;\r\n	display: inline !important;\r\n	padding: 0 !important;\r\n}\r\n\r\n\r\n/* Links dropdown styles */\r\n.dropdown {\r\n  max-width: 13em;\r\n  margin:1px auto 0;\r\n  position: relative;\r\n  float: right;\r\n  width: 100%;\r\n}\r\n\r\n.dropdown-btn {\r\n  background: transparent;\r\n  width: 100%;\r\n  border: none;\r\n  color: #0E4675;\r\n  display: flex;\r\n  align-items: left;\r\n  padding: 0.7em 0.1em;\r\n  cursor: pointer;\r\n}\r\n\r\n.arrow {\r\n  border-left: 5px solid transparent;\r\n  border-right: 5px solid transparent;\r\n  border-top: 6px solid #105289;\r\n  transition: transform ease-in-out 0.3s;\r\n  margin:auto 0; \r\n  float: left;\r\n}\r\n \r\n\r\n.dropdown-content {\r\n  list-style: none;\r\n  position: absolute;\r\n  top: 3.2em;\r\n  width: 100%;\r\n  visibility: hidden;\r\n  overflow: hidden;\r\n}\r\n\r\n.dropdown-content li {\r\n  background: #CFDBE8;\r\n  border-radius: 0.5em;\r\n  position: relative;\r\n  left: 100%;\r\n  transition: 0.5s;\r\n  transition-delay: calc(60ms * var(--delay));\r\n  z-index: 1000;\r\n}\r\n\r\n.dropdown:focus-within .dropdown-content li {\r\n  left: 0;\r\n}\r\n\r\n.dropdown:focus-within .dropdown-content {\r\n  visibility: visible;\r\n}\r\n\r\n.dropdown:focus-within .dropdown-btn > .arrow {\r\n  transform: rotate(180deg);\r\n}\r\n\r\n.dropdown-content li:hover {\r\n  background: #EBEBEB;\r\n  color: #000;\r\n}\r\n\r\n.dropdown-content li a {\r\n  display: block;\r\n  padding: 0.7em 0.5em;\r\n  color: #4D678E;\r\n  margin: 0.1em 0;\r\n  text-decoration: none;\r\n}\r\n\r\n.dropdown-content li a:hover {\r\n  color: #4D678E;\r\n}\r\n\r\n/* Links on gradient backgrounds */\r\n#search-box a:link, .navbg a:link, .forumbg .header a:link, .forabg .header a:link, th a:link {\r\n	color: #FFFFFF;\r\n	text-decoration: none;\r\n}\r\n\r\n#search-box a:visited, .navbg a:visited, .forumbg .header a:visited, .forabg .header a:visited, th a:visited {\r\n	color: #FFFFFF;\r\n	text-decoration: none;\r\n}\r\n\r\n#search-box a:hover, .navbg a:hover, .forumbg .header a:hover, .forabg .header a:hover, th a:hover {\r\n	color: #ffffff;\r\n	text-decoration: underline;\r\n}\r\n\r\n#search-box a:active, .navbg a:active, .forumbg .header a:active, .forabg .header a:active, th a:active {\r\n	color: #ffffff;\r\n	text-decoration: none;\r\n}\r\n\r\n/* Links for forum/topic lists */\r\na.forumtitle {\r\n	font-family: \"Trebuchet MS\", Helvetica, Arial, Sans-serif;\r\n	font-size: 1.2em;\r\n	font-weight: bold;\r\n	color: #898989;\r\n	text-decoration: none;\r\n}\r\n\r\n/* a.forumtitle:visited { color: #898989; } */\r\n\r\na.forumtitle:hover {\r\n	color: #bcbcbc;\r\n	text-decoration: underline;\r\n}\r\n\r\na.forumtitle:active {\r\n	color: #898989;\r\n}\r\n\r\na.topictitle {\r\n	font-family: \"Trebuchet MS\", Helvetica, Arial, Sans-serif;\r\n	font-size: 1.2em;\r\n	font-weight: bold;\r\n	color: #898989;\r\n	text-decoration: none;\r\n}\r\n\r\n/* a.topictitle:visited { color: #d2d2d2; } */\r\n\r\na.topictitle:hover {\r\n	color: #bcbcbc;\r\n	text-decoration: underline;\r\n}\r\n\r\na.topictitle:active {\r\n	color: #898989;\r\n}\r\n\r\n/* Post body links */\r\n.postlink {\r\n	text-decoration: none;\r\n	color: #d2d2d2;\r\n	border-bottom: 1px solid #d2d2d2;\r\n	padding-bottom: 0;\r\n}\r\n\r\n/* .postlink:visited { color: #bdbdbd; } */\r\n\r\n.postlink:active {\r\n	color: #d2d2d2;\r\n}\r\n\r\n.postlink:hover {\r\n	background-color: #f6f6f6;\r\n	text-decoration: none;\r\n	color: #404040;\r\n}\r\n\r\n.signature a, .signature a:visited, .signature a:hover, .signature a:active {\r\n	border: none;\r\n	text-decoration: underline;\r\n	background-color: transparent;\r\n}\r\n\r\n/* Profile links */\r\n.postprofile a:link, .postprofile a:visited, .postprofile dt.author a {\r\n	font-weight: bold;\r\n	color: #898989;\r\n	text-decoration: none;\r\n}\r\n\r\n.postprofile a:hover, .postprofile dt.author a:hover {\r\n	text-decoration: underline;\r\n	color: #d3d3d3;\r\n}\r\n\r\n/* CSS spec requires a:link, a:visited, a:hover and a:active rules to be specified in this order. */\r\n/* See http://www.phpbb.com/bugs/phpbb3/59685 */\r\n.postprofile a:active {\r\n	font-weight: bold;\r\n	color: #898989;\r\n	text-decoration: none;\r\n}\r\n\r\n\r\n/* Profile searchresults */	\r\n.search .postprofile a {\r\n	color: #898989;\r\n	text-decoration: none; \r\n	font-weight: normal;\r\n}\r\n\r\n.search .postprofile a:hover {\r\n	color: #d3d3d3;\r\n	text-decoration: underline; \r\n}\r\n\r\n/* Back to top of page */\r\n.back2top {\r\n	clear: both;\r\n	height: 11px;\r\n	text-align: right;\r\n}\r\n\r\na.top {\r\n	background: none no-repeat top left;\r\n	text-decoration: none;\r\n	width: {IMG_ICON_BACK_TOP_WIDTH}px;\r\n	height: {IMG_ICON_BACK_TOP_HEIGHT}px;\r\n	display: block;\r\n	float: right;\r\n	overflow: hidden;\r\n	letter-spacing: 1000px;\r\n	text-indent: 11px;\r\n}\r\n\r\na.top2 {\r\n	background: none no-repeat 0 50%;\r\n	text-decoration: none;\r\n	padding-left: 15px;\r\n}\r\n\r\n/* Arrow links  */\r\na.up		{ background: none no-repeat left center; }\r\na.down		{ background: none no-repeat right center; }\r\na.left		{ background: none no-repeat 3px 60%; }\r\na.right		{ background: none no-repeat 95% 60%; }\r\n\r\na.up, a.up:link, a.up:active, a.up:visited {\r\n	padding-left: 10px;\r\n	text-decoration: none;\r\n	border-bottom-width: 0;\r\n}\r\n\r\na.up:hover {\r\n	background-position: left top;\r\n	background-color: transparent;\r\n}\r\n\r\na.down, a.down:link, a.down:active, a.down:visited {\r\n	padding-right: 10px;\r\n}\r\n\r\na.down:hover {\r\n	background-position: right bottom;\r\n	text-decoration: none;\r\n}\r\n\r\na.left, a.left:active, a.left:visited {\r\n	padding-left: 12px;\r\n}\r\n\r\na.left:hover {\r\n	color: #d2d2d2;\r\n	text-decoration: none;\r\n	background-position: 0 60%;\r\n}\r\n\r\na.right, a.right:active, a.right:visited {\r\n	padding-right: 12px;\r\n}\r\n\r\na.right:hover {\r\n	color: #d2d2d2;\r\n	text-decoration: none;\r\n	background-position: 100% 60%;\r\n}\r\n\r\n/* invisible skip link, used for accessibility  */\r\n.skiplink {\r\n	position: absolute;\r\n	left: -999px;\r\n	width: 990px;\r\n}\r\n\r\n/* Feed icon in forumlist_body.html */\r\na.feed-icon-forum {\r\n	float: right;\r\n	margin: 3px;\r\n} \n /* END @include links.css */ \n\r\n/* BEGIN @include content.css */ \n /* Content Styles\r\n---------------------------------------- */\r\n\r\nul.topiclist {\r\n	display: block;\r\n	list-style-type: none;\r\n	margin: 0;\r\n}\r\n\r\nul.forums {\r\n	background: #f9f9f9 none repeat-x 0 0;\r\n}\r\n\r\nul.topiclist li {\r\n	display: block;\r\n	list-style-type: none;\r\n	color: #777777;\r\n	margin: 0;\r\n}\r\n\r\nul.topiclist dl {\r\n	position: relative;\r\n}\r\n\r\nul.topiclist li.row dl {\r\n	padding: 2px 0;\r\n}\r\n\r\nul.topiclist dt {\r\n	display: block;\r\n	float: left;\r\n	width: 50%;\r\n	font-size: 1.1em;\r\n	padding-left: 5px;\r\n	padding-right: 5px;\r\n}\r\n\r\nul.topiclist dd {\r\n	display: block;\r\n	float: left;\r\n	border-left: 1px solid #FFFFFF;\r\n	padding: 4px 0;\r\n}\r\n\r\nul.topiclist dfn {\r\n	/* La